        : [...prev.selectedTargets, client.ip]

      return {
        ...prev,
        selectedTargets: newTargets,
      }
    })
  }

  const stopAllAttacks = async () => {
    // Stop all local polling
    Object.keys(pollingIntervals.current).forEach((attackType) => {
      stopPolling(attackType)
    })

