import React, { useState, useEffect } from 'react';

const EvilTwinAttack = () => {
  const [isRunning, setIsRunning] = useState(false);
  const [logs, setLogs] = useState([]);
  const [interfaces, setInterfaces] = useState([]);
  const [selectedInterface, setSelectedInterface] = useState('wlan0');
  const [interfaceInfo, setInterfaceInfo] = useState(null);
  const [capabilities, setCapabilities] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_BASE = 'http://localhost:8000';

  useEffect(() => {
    fetchStatus();
    fetchInterfaces();
    const interval = setInterval(() => {
      fetchStatus();
      fetchLogs();
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  const fetchStatus = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/evil-twin/status`);
      const data = await response.json();
      setIsRunning(data.running);
    } catch (error) {
      console.error('Error fetching status:', error);
    }
  };

  const fetchLogs = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/evil-twin/logs?lines=50`);
      const data = await response.json();
      setLogs(data.logs || []);
    } catch (error) {
      console.error('Error fetching logs:', error);
    }
  };

  const fetchInterfaces = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/evil-twin/interfaces`);
      const data = await response.json();
      setInterfaces(data.interfaces || []);
    } catch (error) {
      console.error('Error fetching interfaces:', error);
    }
  };

  const diagnoseInterface = async (interfaceName) => {
    try {
      setLoading(true);
      const [infoResponse, capabilitiesResponse] = await Promise.all([
        fetch(`${API_BASE}/api/evil-twin/diagnose/${interfaceName}`),
        fetch(`${API_BASE}/api/evil-twin/capabilities/${interfaceName}`)
      ]);

      const infoData = await infoResponse.json();
      const capabilitiesData = await capabilitiesResponse.json();

      setInterfaceInfo(infoData);
      setCapabilities(capabilitiesData);
    } catch (error) {
      console.error('Error diagnosing interface:', error);
      setError('Failed to diagnose interface');
    } finally {
      setLoading(false);
    }
  };

  const startAttack = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await fetch(`${API_BASE}/api/evil-twin/start`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to start attack');
      }

      setIsRunning(true);
    } catch (error) {
      console.error('Error starting attack:', error);
      setError(error.message || 'Failed to start attack');
    } finally {
      setLoading(false);
    }
  };

  const stopAttack = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await fetch(`${API_BASE}/api/evil-twin/stop`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to stop attack');
      }

      setIsRunning(false);
    } catch (error) {
      console.error('Error stopping attack:', error);
      setError(error.message || 'Failed to stop attack');
    } finally {
      setLoading(false);
    }
  };

  const handleInterfaceChange = (e) => {
    const newInterface = e.target.value;
    setSelectedInterface(newInterface);
    diagnoseInterface(newInterface);
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Evil Twin Attack</h2>

      {/* Interface Selection and Diagnostics */}
      <div className="mb-6 p-4 bg-gray-50 rounded-lg">
        <h3 className="text-lg font-semibold mb-4">Interface Configuration</h3>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Select Interface:
          </label>
          <select
            value={selectedInterface}
            onChange={handleInterfaceChange}
            className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            {interfaces.map((iface) => (
              <option key={iface} value={iface}>
                {iface}
              </option>
            ))}
          </select>
        </div>

        {interfaceInfo && (
          <div className="mb-4">
            <h4 className="font-medium text-gray-700 mb-2">Interface Status:</h4>
            <div className="text-sm">
              <p><strong>Exists:</strong> {interfaceInfo.exists ? '‚úÖ Yes' : '‚ùå No'}</p>
              <p><strong>Capabilities:</strong> {interfaceInfo.capabilities.join(', ') || 'None'}</p>
              {interfaceInfo.errors.length > 0 && (
                <div className="mt-2">
                  <p className="text-red-600 font-medium">Errors:</p>
                  <ul className="list-disc list-inside text-red-600">
                    {interfaceInfo.errors.map((error, index) => (
                      <li key={index}>{error}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}

        {capabilities && (
          <div className="mb-4">
            <h4 className="font-medium text-gray-700 mb-2">AP Mode Support:</h4>
            <div className="text-sm">
              <p><strong>Supported:</strong> {capabilities.supported ? '‚úÖ Yes' : '‚ùå No'}</p>
              {capabilities.errors.length > 0 && (
                <div className="mt-2">
                  <p className="text-red-600 font-medium">Issues:</p>
                  <ul className="list-disc list-inside text-red-600">
                    {capabilities.errors.map((error, index) => (
                      <li key={index}>{error}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}

        <button
          onClick={() => diagnoseInterface(selectedInterface)}
          disabled={loading}
          className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
        >
          {loading ? 'Diagnosing...' : 'Diagnose Interface'}
        </button>
      </div>

      {/* Error Display */}
      {error && (
        <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
          <strong>Error:</strong> {error}
        </div>
      )}

      {/* Control Buttons */}
      <div className="mb-6 flex gap-4">
        <button
          onClick={startAttack}
          disabled={isRunning || loading}
          className="px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {loading ? 'Starting...' : 'Start Attack'}
        </button>

        <button
          onClick={stopAttack}
          disabled={!isRunning || loading}
          className="px-6 py-3 bg-red-600 text-white rounded-md hover:bg-red-700 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {loading ? 'Stopping...' : 'Stop Attack'}
        </button>
      </div>

      {/* Status */}
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Status</h3>
        <div className={`inline-block px-3 py-1 rounded-full text-sm font-medium ${
          isRunning ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
        }`}>
          {isRunning ? 'üü¢ Running' : 'üî¥ Stopped'}
        </div>
      </div>

      {/* Logs */}
      <div>
        <h3 className="text-lg font-semibold mb-2">Logs</h3>
        <div className="bg-gray-900 text-green-400 p-4 rounded-md h-64 overflow-y-auto font-mono text-sm">
          {logs.length > 0 ? (
            logs.map((log, index) => (
              <div key={index} className="mb-1">
                {log}
              </div>
            ))
          ) : (
            <div className="text-gray-500">No logs available</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EvilTwinAttack;
