import React, { useState, useEffect } from "react";

const API_BASE = "/api/evil-twin";

const EvilTwinAttack = () => {
  // Form state
  const [ssid, setSsid] = useState("");
  const [iface, setIface] = useState("");
  const [channel, setChannel] = useState("");
  // Status/logs
  const [status, setStatus] = useState(null);
  const [logs, setLogs] = useState([]);
  const [interfaces, setInterfaces] = useState([]);
  // UI state
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  // Diagnostics
  const [interfaceInfo, setInterfaceInfo] = useState(null);
  const [troubleshootingInfo, setTroubleshootingInfo] = useState(null);
  const [showDiagnostics, setShowDiagnostics] = useState(false);

  // Fetch available interfaces (optional, on mount)
  useEffect(() => {
    fetch(`${API_BASE}/interfaces`)
      .then(res => res.json())
      .then(data => setInterfaces(data))
      .catch(() => setInterfaces([]));
  }, []);

  // Fetch status (on mount and after actions)
  const fetchStatus = () => {
    fetch(`${API_BASE}/status`)
      .then(res => res.json())
      .then(data => setStatus(data))
      .catch(() => setStatus(null));
  };

  // Fetch logs
  const fetchLogs = () => {
    fetch(`${API_BASE}/logs?lines=50`)
      .then(res => res.json())
      .then(data => setLogs(data.logs || []))
      .catch(() => setLogs([]));
  };

  // Initial fetch - only status and logs, NO automatic attack start
  useEffect(() => {
    fetchStatus();
    fetchLogs();
  }, []);

  // Handlers
  const handleStart = () => {
    setLoading(true);
    setError(null);
    fetch(`${API_BASE}/start`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ssid, interface: iface, channel })
    })
      .then(res => res.json())
      .then(data => {
        setStatus(data);
        if (data.error) setError(data.error);
        fetchLogs();
      })
      .catch(() => setError("Failed to start attack."))
      .finally(() => setLoading(false));
  };

  const handleStop = () => {
    setLoading(true);
    setError(null);
    fetch(`${API_BASE}/stop`, { method: "POST" })
      .then(res => res.json())
      .then(data => {
        setStatus(data);
        if (data.error) setError(data.error);
        fetchLogs();
      })
      .catch(() => setError("Failed to stop attack."))
      .finally(() => setLoading(false));
  };

  const handleRefreshLogs = () => {
    fetchLogs();
  };

  const handleCheckInterface = () => {
    if (!iface) {
      setError("Please select an interface first");
      return;
    }

    setLoading(true);
    fetch(`${API_BASE}/check-interface/${iface}`)
      .then(res => res.json())
      .then(data => {
        setInterfaceInfo(data);
        if (!data.supported) {
          setError(`Interface issues: ${data.errors.join('; ')}`);
        }
      })
      .catch(() => setError("Failed to check interface"))
      .finally(() => setLoading(false));
  };

  const handleGetTroubleshooting = () => {
    fetch(`${API_BASE}/troubleshooting`)
      .then(res => res.json())
      .then(data => setTroubleshootingInfo(data))
      .catch(() => setError("Failed to get troubleshooting info"));
  };

  const handleGetInterfaceInfo = () => {
    if (!iface) {
      setError("Please select an interface first");
      return;
    }

    fetch(`${API_BASE}/interface-info/${iface}`)
      .then(res => res.json())
      .then(data => setInterfaceInfo(data))
      .catch(() => setError("Failed to get interface info"));
  };

  return (
    <div className="max-w-4xl mx-auto bg-white dark:bg-gray-900 rounded-lg shadow p-8 mt-8">
      <h2 className="text-2xl font-bold mb-6 text-gray-900 dark:text-white flex items-center">
        <span className="inline-block w-2 h-6 bg-red-500 rounded-full mr-3"></span>
        Evil Twin Attack
      </h2>

      {/* Diagnostics Section */}
      <div className="mb-6 p-4 bg-blue-50 dark:bg-blue-900 rounded-lg">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-blue-900 dark:text-blue-100">
            🔧 Diagnostics & Troubleshooting
          </h3>
          <button
            onClick={() => setShowDiagnostics(!showDiagnostics)}
            className="px-3 py-1 bg-blue-200 dark:bg-blue-700 text-blue-800 dark:text-blue-100 rounded hover:bg-blue-300 dark:hover:bg-blue-600 transition"
          >
            {showDiagnostics ? "Hide" : "Show"} Diagnostics
          </button>
        </div>

        {showDiagnostics && (
          <div className="space-y-4">
            <div className="flex gap-2">
              <button
                onClick={handleCheckInterface}
                disabled={loading || !iface}
                className="px-3 py-1 bg-green-200 dark:bg-green-700 text-green-800 dark:text-green-100 rounded hover:bg-green-300 dark:hover:bg-green-600 transition disabled:opacity-50"
              >
                Check Interface
              </button>
              <button
                onClick={handleGetInterfaceInfo}
                disabled={loading || !iface}
                className="px-3 py-1 bg-purple-200 dark:bg-purple-700 text-purple-800 dark:text-purple-100 rounded hover:bg-purple-300 dark:hover:bg-purple-600 transition disabled:opacity-50"
              >
                Interface Info
              </button>
              <button
                onClick={handleGetTroubleshooting}
                disabled={loading}
                className="px-3 py-1 bg-orange-200 dark:bg-orange-700 text-orange-800 dark:text-orange-100 rounded hover:bg-orange-300 dark:hover:bg-orange-600 transition disabled:opacity-50"
              >
                Troubleshooting
              </button>
            </div>

            {interfaceInfo && (
              <div className="bg-white dark:bg-gray-800 rounded p-3">
                <h4 className="font-semibold mb-2">Interface Information:</h4>
                <pre className="text-xs bg-gray-100 dark:bg-gray-700 p-2 rounded overflow-x-auto">
                  {JSON.stringify(interfaceInfo, null, 2)}
                </pre>
              </div>
            )}

            {troubleshootingInfo && (
              <div className="bg-white dark:bg-gray-800 rounded p-3">
                <h4 className="font-semibold mb-2">Troubleshooting Guide:</h4>
                <div className="space-y-2 text-sm">
                  <div>
                    <strong>Common Issues:</strong>
                    <ul className="list-disc list-inside ml-4 mt-1">
                      {troubleshootingInfo.common_issues?.map((issue, i) => (
                        <li key={i}>{issue}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <strong>Recommended Adapters:</strong>
                    <ul className="list-disc list-inside ml-4 mt-1">
                      {troubleshootingInfo.recommended_adapters?.map((adapter, i) => (
                        <li key={i}>{adapter}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>

      <form
        onSubmit={e => {
          e.preventDefault();
          handleStart();
        }}
        className="space-y-4 mb-6"
      >
        <div className="flex flex-col sm:flex-row sm:items-center gap-2">
          <label className="w-28 font-medium text-gray-700 dark:text-gray-200">SSID:</label>
          <input
            type="text"
            value={ssid}
            onChange={e => setSsid(e.target.value)}
            required
            className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-red-400 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white"
            disabled={loading || (status && status.running)}
          />
        </div>
        <div className="flex flex-col sm:flex-row sm:items-center gap-2">
          <label className="w-28 font-medium text-gray-700 dark:text-gray-200">Interface:</label>
          {interfaces.length > 0 ? (
            <select
              value={iface}
              onChange={e => setIface(e.target.value)}
              required
              className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-red-400 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white"
              disabled={loading || (status && status.running)}
            >
              <option value="">Select interface</option>
              {interfaces.map(i => (
                <option key={i} value={i}>{i}</option>
              ))}
            </select>
          ) : (
            <input
              type="text"
              value={iface}
              onChange={e => setIface(e.target.value)}
              required
              className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-red-400 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white"
              disabled={loading || (status && status.running)}
            />
          )}
        </div>
        <div className="flex flex-col sm:flex-row sm:items-center gap-2">
          <label className="w-28 font-medium text-gray-700 dark:text-gray-200">Channel:</label>
          <input
            type="text"
            value={channel}
            onChange={e => setChannel(e.target.value)}
            required
            className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-red-400 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-white"
            disabled={loading || (status && status.running)}
          />
        </div>
        <div className="flex gap-4 mt-4">
          <button
            type="submit"
            disabled={loading || (status && status.running)}
            className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-6 rounded shadow transition disabled:opacity-60 disabled:cursor-not-allowed"
          >
            Start Attack
          </button>
          <button
            type="button"
            onClick={handleStop}
            disabled={loading || !(status && status.running)}
            className="bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-6 rounded shadow transition disabled:opacity-60 disabled:cursor-not-allowed"
          >
            Stop Attack
          </button>
        </div>
      </form>

      {error && (
        <div className="text-red-600 font-medium mb-4 p-3 bg-red-50 dark:bg-red-900 rounded">
          ⚠️ {error}
        </div>
      )}

      <div className="mb-4">
        <strong className="text-gray-800 dark:text-gray-100">Status:</strong>
        <pre className="bg-gray-100 dark:bg-gray-800 rounded p-3 mt-1 text-sm text-gray-900 dark:text-gray-100 overflow-x-auto">
          {status ? JSON.stringify(status, null, 2) : "No status yet."}
        </pre>
      </div>

      <div>
        <div className="flex items-center mb-2">
          <strong className="text-gray-800 dark:text-gray-100">Logs:</strong>
          <button
            onClick={handleRefreshLogs}
            className="ml-4 px-3 py-1 bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-100 rounded hover:bg-gray-300 dark:hover:bg-gray-600 transition"
            type="button"
          >
            Refresh Logs
          </button>
        </div>
        <pre className="bg-gray-100 dark:bg-gray-800 rounded p-3 min-h-[100px] text-xs text-gray-900 dark:text-gray-100 overflow-y-auto max-h-64">
          {logs.length ? logs.join("\n") : "No logs yet."}
        </pre>
      </div>
    </div>
  );
};

export default EvilTwinAttack;
